mapper.update:
update kcd_credit
shzt=#{shzt}, sum_bit=#{sum_bit}, up_time=#{up_time},
where id=#{id}

mapper.select:
select * from kcd_credit where 1=1
and sum_bit=#{sum_bit}
order by add_time desc

select count(*) from kcd_credit
where 1=1 

and sum_bit=#{_parameter}

select * from kcd_credit
where 1=1

and sum_bit=#{_parameter}

limit #{st},#{ps}

select * from kcd_credit
where sum_bit=4
limit #{st},#{ps}

select * from kcd_credit
where sum_bit=5 and shzt=5
limit #{st},#{ps}

select * from kcd_credit
where sum_bit=6 or sum_bit=7
limit #{st},#{ps}

select * from kcd_credit
where  1=1 and mdid=#{mdid}
limit #{st},#{ps}

select * from kcd_credit
where 
name=#{0}
and IDcard_num=#{1}
and phone_num=#{2}
