mapper.update:
 
		update icbc_erp_token 
		
      		
      			bank_id=#{bankId},
      		
      		
      			delmark=#{delmark},
      		
      		
      			mid_edit=#{mid_edit},
      		
      		
      			dt_edit=#{dt_edit} 
      		
      	
		where uid=#{Id}

mapper.select:
select b.* from (select yvi.address,yvi.url,yvi.id yid,yvi.result_id resultId,iekir.result_1_code code,yvi.viedotype, fs.name, ieki.icbc_id icbcid,ieki.id iekiid,ieki.c_name cname,ieki.dt_edit editime,yvi.createtime, yb.id ybId,yb.`name` ybName from icbc_erp_video_info yvi join icbc_erp_kj_icbc ieki on yvi.ext=ieki.icbc_id and ieki.type_id=6 join assess_fs fs on ieki.gems_fs_id=fs.id
left JOIN icbc_erp_kj_icbc_result iekir ON iekir.id=yvi.result_id join kj_icbc ki on ki.id=ieki.icbc_id JOIN icbc_banklist yb on yb.id=ki.bank_id) b
order by yid desc limit ${from},${size}

sql.if:
 		where 1=1
 		
	 	
		   	  and b.code=#{condition.contract}
		
		
		
			
				and b.code is not null
			
			
				and b.code is null
			
		
		
		
		   	  	and b.ybId=#{condition.bank}
