select.if:
select * from icbc_erp_kj_icbc_result 
where 1=1

and icbc_id=#{icbc_id}


and type_id=#{type_id}


and status=#{status}

select icbcmq.*, fs.name as fsname, gems.name as gemsname, ki.c_name as c_name from kjs_icbc_mq icbcmq LEFT JOIN assess_fs fs on fs.id=icbcmq.gems_fs_id LEFT JOIN assess_gems gems on gems.id=icbcmq.gems_id LEFT JOIN kj_icbc ki on ki.id=icbcmq.icbc_id where 1=1 and icbcmq.gems_fs_id=#{fsid}
and icbcmq.bc_status=#{bc_status}
order by icbcmq.dt_edit desc

select icbcdk.*, fs.name as fsname, gems.name as gemsname, ki.c_name as c_name from kjs_icbc_cardk icbcdk LEFT JOIN assess_fs fs on fs.id=icbcdk.gems_fs_id LEFT JOIN assess_gems gems on gems.id=icbcdk.gems_id LEFT JOIN kj_icbc ki on ki.id=icbcdk.icbc_id where 1=1 and icbcdk.gems_fs_id=#{fsid}
and icbcdk.bc_status=#{bc_status}
order by icbcdk.dt_edit desc

select icbckk.*, fs.name as fsname, gems.name as gemsname from kjs_icbc_kk icbckk LEFT JOIN assess_fs fs on fs.id=icbckk.gems_fs_id LEFT JOIN assess_gems gems on gems.id=icbckk.gems_id where 1=1 and icbckk.gems_fs_id=#{fsid}
and icbckk.bc_status=#{bc_status}
order by icbckk.dt_edit desc

select icbcpg.*, fs.name as fsname, gems.name as gemsname, ki.c_name as c_name from assess_cars icbcpg LEFT JOIN assess_fs fs on fs.id=icbcpg.gems_fs_id LEFT JOIN assess_gems gems on gems.id=icbcpg.gems_id LEFT JOIN kj_icbc ki on ki.id=icbcpg.icbc_id where icbcpg.icbc_id>0 and icbcpg.gems_fs_id=#{fsid}
and icbcpg.bc_status=#{bc_status}
order by icbcpg.dt_edit desc

select icbc.*, fs.name as fsname, gems.name as gemsname from kj_icbc icbc LEFT JOIN assess_fs fs on fs.id=icbc.gems_fs_id LEFT JOIN assess_gems gems on gems.id=icbc.gems_id where 1=1 and icbc.gems_fs_id=#{fsid}
and icbc.bc_status=#{bc_status}
order by icbc.dt_edit desc

select * from icbc_erp_kj_icbc_result 
where 1=1

and icbc_id=#{icbc_id}


and type_id=#{type_id}


and status=#{status}

update.if:
update icbc_erp_kj_icbc
mid_add=#{mid_add}, mid_edit=#{mid_edit}, dt_add=#{dt_add}, dt_edit=#{dt_edit}, dt_sub=#{dt_sub}, dt_fin=#{dt_fin}, status=#{status}, icbc_id=#{icbc_id}, gems_id=#{gems_id}, gems_fs_id=#{gems_fs_id}, type_id=#{type_id}, c_name=#{c_name}, c_carno=#{c_carno}, c_carvin=#{c_carvin}, c_cardno=#{c_cardno},
where icbc_id=#{icbc_id} and type_id=#{type_id}
