if.if:
select * from (select admin.*, t3.name as fsname, gems.cp as gemscp, gems.imgurl as gemsimgurl, gems.ssbm as gemsssbm, t3.up_id as fs_up_id, fs1.name as up_name, t.bank_id as t_bank_id, gems.mobile as mobile from ( select t1.*, if(find_in_set(up_id, @pids) > 0, @pids := concat(@pids, ',', id), 0) as ischild from ( select * from assess_fs fs where fs.fs_type=2 and fs.deltag=0 order by fs.up_id,fs.id ) t1, (select @pids :=#{fsid} ) t2 ) t3 LEFT JOIN assess_admin admin ON t3.id=admin.icbc_erp_fsid LEFT JOIN assess_gems gems ON gems.id=admin.gemsid LEFT JOIN assess_fs fs1 ON fs1.id=t3.up_id LEFT JOIN icbc_erp_token t ON t.uid=admin.id where (admin.id and t3.ischild != 0) or t3.id=#{fsid} order by admin.dt_edit desc) t4 where t4.deltag=0
and ( t4.name like concat('%',#{assess_fs_msg},'%') or t4.tel like concat('%',#{assess_fs_msg},'%') or t4.username like concat('%',#{assess_fs_msg},'%') )
and t4.fsname like concat('%',#{assess_ssgs},'%')
and t4.up_name like concat('%',#{assess_ssjg},'%')
and t4.gemscp=#{assess_cp}
and t4.dt_add >= #{date1}
and t4.dt_add <= #{date2}

select.if:
select t4.* from (select t3.*,
admin.name as adminname,
f1.name as fsname,
(select count(*) from assess_admin where icbc_erp_fsid=t3.id and deltag=0 and fs_type=2) as num
 from (
              select t1.*,
              if(find_in_set(up_id, @pids) > 0, @pids := concat(@pids, ',', id), 0) as ischild
              from (
                   select * from assess_fs fs where (fs.fs_type=2 or fs.fs_type=3) and fs.deltag=0 order by fs.up_id,fs.id
                  ) t1,
              (select @pids :=#{fsid} ) t2
) t3 
LEFT JOIN assess_admin admin ON admin.id=t3.mid_add
LEFT JOIN assess_fs f1 ON admin.icbc_erp_fsid=f1.id
where t3.ischild != 0 or t3.id=#{fsid} 
order by t3.dt_edit desc
) t4
where 1=1 

and t4.id=#{assess_fs_id}


and (
   t4.fsname like concat('%',#{assess_fs_msg},'%')
or t4.adminname like concat('%',#{assess_fs_msg},'%')
)


 and t4.dt_add >= #{date1}
 
 
 and t4.dt_add <= #{date2}

select 
agp.*

from icbc_erp_admin_agp agp 
where agp.gems_fs_id=#{fsid} 

and agp.showtag=#{showtag}


and agp.qx_type=#{qx_type}


and agp.modal_tag=#{modal_tag}


and agp.name like concat('%',#{assess_fs_msg},'%')


 and agp.dt_add >= #{date1}
 
 
 and agp.dt_add <= #{date2}
 

order by agp.dt_edit desc

update.if:
update assess_fs
name=#{name}, name_qy=#{name_qy}, showtag=#{showtag}, state_id=#{state_id}, city_id=#{city_id}, address=#{address}, code_pre=#{code_pre}, namepy=#{namepy}, dt_edit=#{dt_edit}, mid_edit=#{mid_edit}, oemimgurl=#{oemimgurl}, purview_map=#{purview_map}, purview_map_kjs=#{purview_map_kjs}, icbc_erp_tag=#{icbc_erp_tag}, bank_id={bank_id}, fs_type={fs_type},
where id=#{id}

update assess_gems
fsid=#{fsid}, name=#{name}, mobile=#{mobile}, update_time=#{update_time}, cp=#{cp}, username=#{username}, dt_edit=#{dt_edit}, idcard=#{idcard}, upac_id=#{upac_id}, imgurl=#{imgurl}, ssbm=#{ssbm},
where id=#{id}

update assess_admin
mid_edit=#{mid_edit}, dt_edit=#{dt_edit}, username=#{username}, userpass=#{userpass}, tel=#{tel}, email=#{email}, name=#{name}, agpid=#{agpid}, icbc_erp_fsid=#{icbc_erp_fsid}, bc_title=#{bc_title}, upac_id=#{upac_id}, cp=#{cp}, erp_agpid=#{erp_agpid}, login_type=#{login_type},
where id=#{id}

update icbc_erp_admin_agp
name=#{name}, purview_map=#{purview_map}, mid_edit=#{mid_edit}, dt_edit=#{dt_edit}, showtag=#{showtag}, modal_tag=#{modal_tag}, gems_fs_id=#{gems_fs_id}, qx_type=#{qx_type},
where id=#{id}
