mapper.select:
select dk.* ,fs.`name` as gname, gems.`name` as pname, ki.c_name as c_name from kjs_icbc_cardk dk LEFT JOIN assess_fs fs on dk.gems_fs_id=fs.id LEFT JOIN assess_gems gems on dk.gems_id=gems.id LEFT JOIN kj_icbc ki on ki.id=dk.icbc_id where 1=1 and dk.icbc_id>0 and dk.bc_status>1
and dk.bc_status=#{bc_status}
ORDER BY dk.dt_edit DESC

mapper.update:
update kjs_icbc_cardk
mid_edit=#{mid_edit}, dt_edit=#{dt_edit}, bc_status=#{bc_status}, sh_status=#{sh_status}, fk_status=#{fk_status}, dt_fin=#{dt_fin},
where id=#{id}

update kjs_icbc_cardk
result=#{result}, result_time=#{result_time}, api_edit=#{api_edit}, bc_status=#{bc_status}, gems_code=#{gems_code}, query_type=#{query_type}, dt_fin=#{dt_fin}, dt_sub=#{dt_sub}, dt_edit=#{dt_edit}, dt_add=#{dt_add}, mid_edit=#{mid_edit}, mid_add=#{mid_add}, gems_id=#{gems_id}, gems_fs_id=#{gems_fs_id}, imgstep4_1ss=#{imgstep4_1ss}, imgstep4_2ss=#{imgstep4_2ss}, imgstep4_3ss=#{imgstep4_3ss}, imgstep4_4ss=#{imgstep4_4ss}, imgstep4_5ss=#{imgstep4_5ss}, imgstep5_1ss=#{imgstep5_1ss}, imgstep6_1ss=#{imgstep6_1ss}, imgstep7_1ss=#{imgstep7_1ss},
where id=#{id}
