mapper.select:
select kk.* ,fs.`name` as gname, gems.`name` as pname from kjs_icbc_kk kk LEFT JOIN assess_fs fs on kk.gems_fs_id=fs.id LEFT JOIN assess_gems gems on kk.gems_id=gems.id where 1=1 and kk.icbc_id>0 and kk.bc_status>1
and kk.bc_status=#{bc_status}
ORDER BY kk.dt_edit DESC

mapper.update:
update kjs_icbc_kk
gems_code=#{gems_code}, c_name=#{c_name}, c_tel=#{c_tel}, cars_type=#{cars_type}, kp_price=#{kp_price}, dk_price=#{dk_price}, jrfw_price=#{jrfw_price}, dk_total_price=#{dk_total_price}, aj_type=#{aj_type}, aj_date=#{aj_date}, aj_bank=#{aj_bank}, dk_lv=#{dk_lv}, dz_type=#{dz_type}, sp_address=#{sp_address}, yw_address=#{yw_address}, img_1=#{img_1}, img_2=#{img_2}, img_3=#{img_3}, img_4=#{img_4}, img_5=#{img_5}, img_6=#{img_6}, img_1s=#{img_1s}, up_time=#{up_time}, bc_status=#{bc_status}, md_edit=#{md_edit}, api_edit=#{api_edit}, sh_edit=#{sh_edit}, result=#{result}, result_time=#{result_time}, dt_edit=#{dt_edit}, dt_sub=#{dt_sub}, dt_fin=#{dt_fin}, query_type=#{query_type}, mid_edit=#{mid_edit}, kk_car_stateid=#{kk_car_stateid}, kk_car_cityid=#{kk_car_cityid}, kk_loan_stateid=#{kk_loan_stateid}, kk_loan_cityid=#{kk_loan_cityid}, kk_kh=#{kk_kh}, imgstep3_1=#{imgstep3_1}, imgstep3_2=#{imgstep3_2}, imgstep3_3=#{imgstep3_3}, imgstep3_4=#{imgstep3_4}, imgstep3_5=#{imgstep3_5}, imgstep3_6=#{imgstep3_6}, imgstep3_7=#{imgstep3_7}, imgstep3_7s=#{imgstep3_7s}, pdfstep4_1=#{pdfstep4_1}, pdf_time=#{pdf_time},
where id=#{id}
