mapper.select:
SELECT icbc.id as icbc_id, icbc.gems_id as gems_id, icbc.gems_fs_id as gems_fs_id, icbc.c_name as c_name, icbc.c_cardno as c_cardno, cars.c_carno as c_carno, cars.vincode as c_carvin FROM kj_icbc icbc LEFT JOIN assess_cars cars ON icbc.id=cars.icbc_id WHERE 1=1 and
c_cardno =#{c_cardno}
icbc.id =#{id}
limit 0,1

select * from loan_import_record where 1=1 and gems_fs_id=#{gems_fs_id}
oriName like '%${param}%'
order by dt_add desc
