mapper.select:
select lol.*, bc.gems_code, kk.dk_total_price, yb.name bank_name, fs.name fs_name, gems.name gems_name, ac.c_carno c_carno, ac.cars_type cars_type from loan_overdue_list lol LEFT JOIN kj_icbc bc ON bc.id=lol.icbc_id LEFT JOIN kjs_icbc_kk kk ON kk.icbc_id=lol.icbc_id LEFT JOIN yl_banklist yb ON bc.bank_id = yb.id LEFT JOIN assess_fs fs on lol.gems_fs_id=fs.id LEFT JOIN assess_gems gems on lol.gems_id=gems.id LEFT JOIN assess_cars ac on ac.icbc_id=bc.id where 1=1 and 2=2 and lol.type_id=#{type_id} and lol.gems_fs_id=#{gems_fs_id}
and lol.type_status=#{type_status}
and (lol.c_name like '%${param}%' or lol.c_cardno like '%${param}%')
ORDER BY lol.dt_edit DESC

mapper.update:
update loan_overdue_list set
	type_id=#{type_id},
	dt_edit=sysdate(),
	mid_edit=#{mid_edit}

	,type_status=#{type_status}

where 
	id=#{id}

mapper.select:
select lol.*, bc.gems_code, lc.* from loan_overdue_list lol LEFT JOIN kj_icbc bc ON bc.id=lol.icbc_id LEFT JOIN loan_config lc ON lc.gems_fs_id=lol.gems_fs_id where 1=1 and lol.type_id=1 and lol.gems_fs_id=#{gems_fs_id}
and (lol.c_name like '%${param}%' or lol.c_cardno like '%${param}%')
ORDER BY lol.dt_edit DESC
