mapper.select:
select con.*, kk.dk_total_price, kk.aj_date, kk.dk_lv, (select count(*) from import_repayment re where re.id_card=bc.c_cardno) as jd_count from confirm_compensatory con left join kj_icbc bc on con.id=bc.id left join kjs_icbc_kk kk on kk.icbc_id=bc.id where 1=1
and con.c_name like '%${param}%' OR con.id_card like '%${param}%'
limit #{pagenow},#{pagesize}

mapper.update:
update confirm_compenatory2 
	set compensatory_state='已同意申请',compensatory_date=#{dcdate}
	where 
	confirm_idCard=#{id_card} and repayment_periods=#{periods}

update confirm_compensatory set advance_payment=#{count} 
where
id_card=#{id_card}

update agree_compensate set advance_payment=#{count} 
where
id_card=#{id_card}
